import React from 'react';

const Card = (prop) => {
  let inputField;
  // 2. Assign the variable to the input component
  // 3. Upon onClick event, alert the contents of the input field

  const registerEmail = () => {

      //console.log(inputField)
      fetch('http://localhost:8081/emails/register',
      {
          method: 'POST',
          body: JSON.stringify({email: inputField.value}),
          headers: {"Content-Type": "application/json"}
      }

      )
          .then(response => response.json())
          .then(json => console.log('response from backend', json))

  }
    return (
        <div className="card" style={{width: "18rem"}}>
          <img 
            src={prop.image} 
            className="card-img-top" 
            alt={prop.title}/>
          <div className="card-body">
            <h5 className="card-title">{prop.title}</h5>
            <p className="card-text">{prop.description}</p>
            <a 
            href={prop.buttonLink} 
            className="btn btn-primary">
            {prop.buttonLabel}
            </a>
            <div className="input-group-append">
                <button 
                    className="btn btn-outline-secondary" 
                    type="button" 
                    id="button-addon2"
                    onClick={makePurchase}
                >Purchase</button> </div>
          </div>
        </div>
    )
}

export default Card;
