import React from 'react';
import NavBar from './NavBar.js';

const RegistrationPage = () => {

    // Add ref prop to all input fields
    let firstName;
    let lastName;
    let email;
    let password;

    // Connected to globalState
    const [globalState, setGlobalState] = useContext();

    // A local state
    const [state, setState] = useState(
        {
            loading: false
        }
    )

const registerUser = () => {

    fetch('http://localhost:8081/users/register', 
        {
            method: 'POST',
            body: JSON.stringify({
                firstName: firstNameField.value,
                lastName: lastNameField.value,
                email: emailField.value,
                password: passwordField.value
            }),
            headers: {"Content-Type": "application/json"}
        }
    )
    .then(
        (result) => result.json()
    )
    setState({...state, loading: true})
    .then (
        // Start loading
        (json) => {
            const { message, jsonwebtoken } = json;
            if(jsonwebtoken) {
                // update the globalState
                setGlobalState(
                    {
                        ...globalState,
                        registered: true
                    }
                )

                setState({...state, loading: false})
            } else {
                // throw an error
                alert(message);
            }
        }
    )
}

    // If the user is registered, redirect them
    if(globalState.registered === true) {
        return(<Redirect to="/login"/>)
    }

    // Otherwise, show the login form
    else {
        return(
            <div>
                <NavBar />
                <h1>Registration</h1>

                <div className="container">
                    <div className="row">
                        <div className="col-sm" 
                        style={{maxWidth: '400px', margin: '0 auto'}}>
                            <div>


                                <div className="form-group">
                                    <label>
                                        First Name
                                    </label>

                                    <input 
                                    ref={(comp)=>firstName}
                                    type="text" 
                                    className="form-control" 
                                    aria-describedby="firstName"/>
                                </div>

                                <div className="form-group">
                                    <label>
                                        Last Name
                                    </label>

                                    <input 
                                    type="text" 
                                    className="form-control" 
                                    aria-describedby="lastName"/>
                                </div>

                                <div className="form-group">
                                    <label htmlFor="exampleInputEmail1">
                                        Email address
                                    </label>

                                    <input 
                                    type="email" 
                                    className="form-control" 
                                    id="exampleInputEmail1" 
                                    aria-describedby="emailHelp"/>

                                    <small id="emailHelp" className="form-text text-muted">We'll never share your email with anyone else.</small>
                                </div>

                                <div className="form-group">
                                    <label>
                                        Password
                                    </label>

                                    <input 
                                    type="password" 
                                    className="form-control" 
                                    aria-describedby="password"/>
                                </div>

                                <button
                                type="button"
                                className="btn btn-primary">Register
                                </button>
                        </div>
                    </div>
                </div>
              </div>
            </div>
        )
    }
}

export default RegistrationPage;
